root.uniform:
samplerCube EnvCubemap;
CameraInfo g_camera;
SceneInfo r_scene_info;

root.main:
vec3 I = normalize(Position-g_camera.pos);
vec3 R = reflect (I*vec3(1,-1,1), normalize(Normal));

R=vec3(inverse(r_scene_info.l2w)*vec4(R,0.0));
// Convert cubemap coordinates into Vulkan coordinate space
R.y*=-1.0;

FragColor = texture(EnvCubemap, R.xzy, 1.0);
